$(document).ready(function() {
    hoverAnimated(), hamburgerClick(), gMap();
}), $(window).on("load", function() {}), $(window).on("resize", function() {});

var hoverAnimated = function() {
    $(".arrow").length > 0 && $(".arrow").click(function() {
        $("html, body").animate({
            scrollTop: $(".h-com").offset().top - 40
        }, 800);
    });
}, hamburgerClick = function() {
    $("#nav-ico").click(function() {
        $("#nav-ico").toggleClass("open"), $(".m-box").slideToggle(), $("body").toggleClass("noscroll");
    });
}, gMap = function() {
    function a() {
        var a = new google.maps.LatLng(41.110251, 29.01924), b = document.getElementById("map"), c = {
            center: a,
            zoom: 16,
            panControl: !1,
            mapTypeId: google.maps.MapTypeId.ROADMAP
        };
        new google.maps.Map(b, c);
    }
    google.maps.event.addDomListener(window, "load", a);
};